(def hero
  [:section {:class "row py-5 align-items-center justify-content-center"}
   [:div {:class "col-md-4 text-center text-md-left"}
    [:h1 {:class "display-3"} "Intention"]
    [:p.lead "Plan and Achieve your Goals with Intention"]
